// Add these functions INSIDE your VideoGenerationApp component, before the return statement

const handleWatchVideo = (video) => {
    if (video.video_url) {
      // Open video in new tab
      window.open(video.video_url, '_blank');
    } else {
      alert(`üé¨ "${video.title}"\n\nVideo URL not available yet.\nStatus: ${video.status}`);
    }
  };
  
  const handleDownloadVideo = (video) => {
    if (video.video_url) {
      // Create download link
      const link = document.createElement('a');
      link.href = video.video_url;
      link.download = `${video.title.replace(/[^a-zA-Z0-9]/g, '_')}.mp4`;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
    } else {
      alert(`üì• "${video.title}"\n\nVideo not ready for download.\nStatus: ${video.status}`);
    }
  };
  
  // Update your button JSX to include onClick handlers:
  {video.status === 'completed' && (
    <div style={{ display: 'flex', gap: '0.5rem' }}>
      <button 
        onClick={() => handleWatchVideo(video)}
        style={{ 
          flex: 1, 
          background: '#22c55e', 
          color: 'white', 
          padding: '0.5rem 1rem', 
          borderRadius: '6px', 
          border: 'none', 
          fontSize: '0.875rem', 
          fontWeight: '500',
          cursor: 'pointer',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          gap: '0.25rem'
        }}
      >
        <span>‚ñ∂Ô∏è</span>
        <span>Watch</span>
      </button>
      <button 
        onClick={() => handleDownloadVideo(video)}
        style={{ 
          flex: 1, 
          background: '#3b82f6', 
          color: 'white', 
          padding: '0.5rem 1rem', 
          borderRadius: '6px', 
          border: 'none', 
          fontSize: '0.875rem', 
          fontWeight: '500',
          cursor: 'pointer',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          gap: '0.25rem'
        }}
      >
        <span>‚¨áÔ∏è</span>
        <span>Download</span>
      </button>
    </div>
  )}
