import React, { useState, useEffect } from 'react';

const API_BASE_URL = 'http://localhost:5001/api';

const VideoGenerationApp = () => {
  const [prompt, setPrompt] = useState('');
  const [duration, setDuration] = useState(10);
  const [videos, setVideos] = useState([]);
  const [isGenerating, setIsGenerating] = useState(false);
  const [backendStatus, setBackendStatus] = useState('checking');

  // Check backend health on component mount
  useEffect(() => {
    checkBackendHealth();
    fetchVideos();
  }, []);

  const checkBackendHealth = async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/health`);
      const data = await response.json();
      setBackendStatus(data.status === 'ok' ? 'connected' : 'error');
    } catch (error) {
      console.error('Backend health check failed:', error);
      setBackendStatus('error');
    }
  };

  const fetchVideos = async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/videos`);
      const data = await response.json();
      setVideos(data);
    } catch (error) {
      console.error('Failed to fetch videos:', error);
    }
  };

  const generateVideo = async (e) => {
    e.preventDefault();
    if (!prompt.trim()) return;

    setIsGenerating(true);
    try {
      const response = await fetch(`${API_BASE_URL}/videos`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          prompt: prompt.trim(),
          duration: duration
        }),
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const newVideo = await response.json();
      setVideos(prev => [newVideo, ...prev]);
      setPrompt('');
      
      // Simulate processing updates (replace with real RunwayML integration later)
      setTimeout(() => updateVideoStatus(newVideo.id, 'processing'), 1000);
      setTimeout(() => updateVideoStatus(newVideo.id, 'completed'), 5000);
      
    } catch (error) {
      console.error('Error generating video:', error);
      alert('Failed to generate video. Please try again.');
    } finally {
      setIsGenerating(false);
    }
  };

  const updateVideoStatus = async (id, status) => {
    try {
      const response = await fetch(`${API_BASE_URL}/videos/${id}`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ status }),
      });

      if (response.ok) {
        const updatedVideo = await response.json();
        setVideos(prev => 
          prev.map(video => 
            video.id === id ? updatedVideo : video
          )
        );
      }
    } catch (error) {
      console.error('Error updating video status:', error);
    }
  };

  return (
    <div style={{ 
      minHeight: '100vh', 
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      color: 'white',
      fontFamily: 'Arial, sans-serif'
    }}>
      {/* Header */}
      <div style={{ 
        background: 'rgba(0,0,0,0.2)', 
        padding: '1rem',
        borderBottom: '1px solid rgba(255,255,255,0.1)'
      }}>
        <div style={{ maxWidth: '1200px', margin: '0 auto', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
          <div style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
            <div style={{ 
              background: 'linear-gradient(45deg, #9333ea, #ec4899)', 
              padding: '0.5rem', 
              borderRadius: '8px',
              fontSize: '1.5rem'
            }}>
              🎬
            </div>
            <div>
              <h1 style={{ margin: 0, fontSize: '1.5rem', fontWeight: 'bold' }}>AI Video Studio</h1>
              <p style={{ margin: 0, fontSize: '0.875rem', opacity: 0.8 }}>Powered by RunwayML</p>
            </div>
          </div>
          
          {/* Backend Status Indicator */}
          <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
            <div style={{ 
              width: '12px', 
              height: '12px', 
              borderRadius: '50%', 
              background: backendStatus === 'connected' ? '#22c55e' : 
                         backendStatus === 'error' ? '#ef4444' : '#eab308'
            }}></div>
            <span style={{ fontSize: '0.875rem' }}>
              Backend: {backendStatus === 'connected' ? 'Connected' : 
                       backendStatus === 'error' ? 'Disconnected' : 'Checking...'}
            </span>
          </div>
        </div>
      </div>

      <div style={{ maxWidth: '1200px', margin: '0 auto', padding: '2rem' }}>
        {/* Video Generation Form */}
        <div style={{ 
          background: 'rgba(255,255,255,0.1)', 
          borderRadius: '16px', 
          padding: '2rem', 
          marginBottom: '2rem',
          backdropFilter: 'blur(10px)'
        }}>
          <div style={{ display: 'flex', alignItems: 'center', gap: '0.75rem', marginBottom: '1.5rem' }}>
            <span style={{ fontSize: '1.5rem' }}>✨</span>
            <h2 style={{ margin: 0, fontSize: '1.5rem', fontWeight: '600' }}>Generate New Video</h2>
          </div>
          
          <form onSubmit={generateVideo} style={{ display: 'flex', flexDirection: 'column', gap: '1.5rem' }}>
            <div>
              <label style={{ display: 'block', fontSize: '0.875rem', fontWeight: '500', marginBottom: '0.75rem', opacity: 0.9 }}>
                Video Prompt
              </label>
              <textarea
                value={prompt}
                onChange={(e) => setPrompt(e.target.value)}
                placeholder="Describe the video you want to generate... (e.g., 'A majestic eagle soaring over mountain peaks at sunset')"
                style={{
                  width: '100%',
                  padding: '0.75rem',
                  background: 'rgba(255,255,255,0.1)',
                  border: '1px solid rgba(255,255,255,0.2)',
                  borderRadius: '8px',
                  color: 'white',
                  fontSize: '1rem',
                  resize: 'none',
                  outline: 'none'
                }}
                rows="3"
                disabled={isGenerating}
                required
              />
            </div>

            <div>
              <label style={{ display: 'block', fontSize: '0.875rem', fontWeight: '500', marginBottom: '0.75rem', opacity: 0.9 }}>
                Duration: {duration} seconds
              </label>
              <input
                type="range"
                min="5"
                max="30"
                step="5"
                value={duration}
                onChange={(e) => setDuration(Number(e.target.value))}
                style={{ width: '100%', height: '8px', background: 'rgba(255,255,255,0.2)', borderRadius: '4px' }}
                disabled={isGenerating}
              />
              <div style={{ display: 'flex', justifyContent: 'space-between', fontSize: '0.75rem', opacity: 0.7, marginTop: '0.25rem' }}>
                <span>5s</span>
                <span>15s</span>
                <span>30s</span>
              </div>
            </div>

            <button
              type="submit"
              disabled={isGenerating || !prompt.trim() || backendStatus !== 'connected'}
              style={{
                width: '100%',
                background: isGenerating || !prompt.trim() || backendStatus !== 'connected' 
                  ? 'rgba(107, 114, 128, 0.8)' 
                  : 'linear-gradient(45deg, #9333ea, #ec4899)',
                color: 'white',
                fontWeight: '600',
                padding: '1rem 1.5rem',
                borderRadius: '8px',
                border: 'none',
                cursor: isGenerating || !prompt.trim() || backendStatus !== 'connected' ? 'not-allowed' : 'pointer',
                fontSize: '1rem',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                gap: '0.5rem'
              }}
            >
              {isGenerating ? (
                <>
                  <span style={{ animation: 'spin 1s linear infinite' }}>⏳</span>
                  <span>Generating Video...</span>
                </>
              ) : (
                <>
                  <span>▶️</span>
                  <span>Generate Video</span>
                </>
              )}
            </button>
          </form>
        </div>

        {/* Videos List */}
        <div style={{ 
          background: 'rgba(255,255,255,0.1)', 
          borderRadius: '16px', 
          padding: '2rem',
          backdropFilter: 'blur(10px)'
        }}>
          <h3 style={{ margin: '0 0 1.5rem 0', fontSize: '1.25rem', fontWeight: '600', display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
            <span>🎬</span>
            <span>Your Videos ({videos.length})</span>
          </h3>

          {videos.length === 0 ? (
            <div style={{ textAlign: 'center', padding: '3rem 0' }}>
              <div style={{ 
                background: 'rgba(147, 51, 234, 0.2)', 
                borderRadius: '50%', 
                width: '64px', 
                height: '64px', 
                display: 'flex', 
                alignItems: 'center', 
                justifyContent: 'center', 
                margin: '0 auto 1rem',
                fontSize: '2rem'
              }}>
                🎬
              </div>
              <p style={{ fontSize: '1.125rem', margin: '0 0 0.5rem 0' }}>No videos generated yet</p>
              <p style={{ fontSize: '0.875rem', opacity: 0.7, margin: 0 }}>Create your first AI-generated video above!</p>
            </div>
          ) : (
            <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))', gap: '1.5rem' }}>
              {videos.map((video) => (
                <div key={video.id} style={{ 
                  background: 'rgba(255,255,255,0.05)', 
                  borderRadius: '8px', 
                  border: '1px solid rgba(255,255,255,0.1)', 
                  padding: '1.5rem',
                  transition: 'background 0.2s'
                }}>
                  <div style={{ display: 'flex', alignItems: 'flex-start', justifyContent: 'space-between', marginBottom: '1rem' }}>
                    <div style={{ 
                      padding: '0.25rem 0.75rem', 
                      borderRadius: '1rem', 
                      fontSize: '0.75rem', 
                      fontWeight: '500',
                      background: video.status === 'pending' ? 'rgba(234, 179, 8, 0.2)' :
                                 video.status === 'processing' ? 'rgba(59, 130, 246, 0.2)' :
                                 video.status === 'completed' ? 'rgba(34, 197, 94, 0.2)' : 'rgba(107, 114, 128, 0.2)',
                      color: video.status === 'pending' ? '#eab308' :
                             video.status === 'processing' ? '#3b82f6' :
                             video.status === 'completed' ? '#22c55e' : '#6b7280',
                      display: 'flex',
                      alignItems: 'center',
                      gap: '0.25rem'
                    }}>
                      <span>
                        {video.status === 'pending' ? '🕐' :
                         video.status === 'processing' ? '⏳' :
                         video.status === 'completed' ? '✅' : '❌'}
                      </span>
                      <span style={{ textTransform: 'capitalize' }}>{video.status}</span>
                    </div>
                    <span style={{ fontSize: '0.75rem', opacity: 0.7 }}>
                      {new Date(video.created_at).toLocaleDateString()}
                    </span>
                  </div>

                  <p style={{ fontSize: '0.875rem', marginBottom: '1rem', lineHeight: 1.5 }}>
                    {video.prompt}
                  </p>

                  <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', fontSize: '0.75rem', opacity: 0.7, marginBottom: '1rem' }}>
                    <span>Duration: {video.duration}s</span>
                    <span>ID: #{video.id}</span>
                  </div>

                  {video.status === 'completed' && (
                    <div style={{ display: 'flex', gap: '0.5rem' }}>
                      <button style={{ 
                        flex: 1, 
                        background: '#22c55e', 
                        color: 'white', 
                        padding: '0.5rem 1rem', 
                        borderRadius: '6px', 
                        border: 'none', 
                        fontSize: '0.875rem', 
                        fontWeight: '500',
                        cursor: 'pointer',
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center',
                        gap: '0.25rem'
                      }}>
                        <span>▶️</span>
                        <span>Watch</span>
                      </button>
                      <button style={{ 
                        flex: 1, 
                        background: '#3b82f6', 
                        color: 'white', 
                        padding: '0.5rem 1rem', 
                        borderRadius: '6px', 
                        border: 'none', 
                        fontSize: '0.875rem', 
                        fontWeight: '500',
                        cursor: 'pointer',
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center',
                        gap: '0.25rem'
                      }}>
                        <span>⬇️</span>
                        <span>Download</span>
                      </button>
                    </div>
                  )}
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default VideoGenerationApp;
